import { Button, ButtonGroup, TextField } from '@mui/material';
import React from 'react'
import { useState } from 'react'

export default function ItemCount() {

    const[contar, setCont] = useState(1);
    const decreser =() =>{
        setCont(contar - 1);
    }

    const incrementar =() =>{
        setCont(contar + 1);
    }
return (
    <>
    <div>
        <ButtonGroup variant="contained" arial-label="outlined primary buttom group">
            <Button disable={contar <= 1} onClick={decreser}>-</Button>
            <TextField id="outlined-number" label="Cantidad" value={contar} variant="outlined"></TextField>
            <Button disable={contar >= stock}onClick={incrementar}>+</Button>
        </ButtonGroup>
        <Button>Agregar al carrito</Button>
    </div>
    </>
)
}
